import React from "react";
import { Link } from "react-router-dom";
import { useCartContext } from "../Context/CartContext";
import ItemCart from "../ItemCart/ItemCart";
import "./Cart.css";

const Cart = () => {
  const { cart, totalPrice } = useCartContext();

  // Verifica si el carrito está vacío
  const isCartEmpty = cart.length === 0;

  return (
    <div className={isCartEmpty ? "cart1" : ""}>
      {isCartEmpty ? (
        <div className="empty-cart-message">
          <p>Sin productos en el carrito</p>
          <Link to="/" className="back-to-shopping-link">
            Volver a comprar
          </Link>
        </div>
      ) : (
        // Renderiza aquí el contenido del carrito cuando no está vacío
        <div>
          {cart.map((product) => (
            <ItemCart key={product.id} product={product} />
          ))}
          <p className="total-price">Total: $ {totalPrice()}</p>
          <Link to="/checkout">
            <button className="btn-total">Finalizar Compra</button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default Cart;
