import React, { useState, useEffect } from "react";
import Card from "../Cards/Card";
import '../Cards/CardSty.css';

const CardContainer = () => {
  const [personaje, setPersonaje] = useState([]);

  useEffect(() => {
    async function getPersonaje() {
      try {
        const response = await fetch(
          `https://rickandmortyapi.com/api/character`
        );
        if (response.ok) {
          //.OK palabra reservada, indica que la palabra deseada es la correcta
          const data = await response.json();
          setPersonaje(data.results);
        } else {
          console.error(`error al cargar personajes`);
        }
      } catch (error) {
        console.error(`Error al cargar personajes`, error);
      }
    }
    getPersonaje();
  }, []); //Sintasis de useEffect, se deja [] para completar dependencia

  return (
    <div>
      <h2>Personajes Rick and Morty</h2>
      {personaje ? (
        personaje.map((p) => <Card key={p.div} character={p} />)
      ) : (
        <p>Cargar personaje...</p>
      )} 
    </div>
  );
};

export default CardContainer;
