import React, { useState, useEffect } from 'react'

const ApiMl = () => {

    const [products, setProducts] = useState([]);

    useEffect(() => {
        async function fetchProducts(){
            try {
                const response = await fetch(`https://api.mercadolibre.com/sites/MLA/search?q=producto`);
                if (response.ok) { //.OK palabra reservada, indica que la palabra deseada es la correcta
                    const data = await response.json();
                    const firstTenProducts = data.results.slice(0, 10);
                    setProducts(firstTenProducts);
                } else {
                    console.error(`error al cargar datos de productos`);
                }
            }
            catch (error) {
                console.error(`Error al cargar datos de productos`, error);

            }
        }
        fetchProducts();
    }, []) //Sintasis de useEffect, se deja [] para completar dependencia



    return (
        <div>
            <h2>Productos de Mercado Libre</h2>

            <ul>
{products.map((p)=>(
<li key={p.id}>
    {p.title}
</li>
))}
            </ul>
        </div>
    )
}

export default ApiMl
