import React from "react";
import { Link } from "react-router-dom";
import { useCartContext } from "../Context/CartContext";
import ItemCart from "../ItemCart/ItemCart";
import "./Cart.css";

const Cart = () => {
  const { cart, totalPrice, clearCart } = useCartContext();

  const handleCheckout = () => {
    clearCart();
  };

  const emptyCartImageSrc =
    "https://img.freepik.com/fotos-premium/carrito-compras-neon-letrero-neon-que-dice-soy-tienda_883586-9014.jpg";

  if (cart.length === 0) {
    return (
      <div className="cart1">
        <div className="empty-cart-message">
          <img
            src={emptyCartImageSrc}
            alt="Carrito vacío"
            className="empty-cart-image" // Aplica la clase CSS
          />
          <p>Sin productos en el carrito</p>
          <Link to="/" className="back-to-shopping-link">
            Volver a comprar
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      {cart.length === 0 ? (
        <div className="empty-cart-message text-center">
          <img
            src={emptyCartImageSrc}
            alt="Carrito vacío"
            className="img-fluid empty-cart-image mb-3"
          />
          <p>¡ Sin productos en el carrito !</p>
          <Link to="/" className="btn btn-primary">
            Volver a comprar
          </Link>
        </div>
      ) : (
        <div className="card cart-container">
          <div className="card-body">
            {cart.map((product) => (
              <div key={product.id} className="cart-product mb-3">
                <ItemCart product={product} />
              </div>
            ))}
            <p className="total-price">Total: $ {totalPrice()}</p>
            <Link to="/checkout">
              <button
                onClick={handleCheckout}
                className="btn btn-success btn-block"
              >
                Finalizar Compra
              </button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
            }  

export default Cart;
