import React from "react";
import { useState } from "react";

const ItemCount = ({ stockItems, onAddToCart }) => {
  const [counter, setCounter] = useState(1);
  const [stock] = useState(stockItems);

  const incrementarStock = () => {
    if (counter < stock) {
      setCounter(counter + 1);
    }
  };

  const decrementarStock = () => {
    if (counter > 1) {
      setCounter(counter - 1);
    }
  };

  const handleAddToCart = () => {
    // Llama a la función onAddToCart con la cantidad actual
    onAddToCart(counter);
  };

  return (
    <div className="container" id="itemcount">
      <div className="row mb-3">
        <div className="col-md-2">
          <div className="btn-group" role="group" aria-label="Basic outlined example">
            <button type="button" className="btn btn-outline-primary" onClick={decrementarStock}>
              -
            </button>
            <button type="button" className="btn btn-outline-primary">
              {counter}
            </button>
            <button type="button" className="btn btn-outline-primary" onClick={incrementarStock}>
              +
            </button>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col-md-2">
          <button type="button" className="btn btn-outline-primary" onClick={handleAddToCart}>
            Agregar al carrito
          </button>
        </div>
      </div>
    </div>
  );
};

export default ItemCount;
