import React, { useState } from "react";
import ItemCount from "../ItemCount/ItemCount";

const ItemDetail = ({ item }) => {
  const [cartCount, setCartCount] = useState(0); // Estado para la cantidad en el carrito

  const handleAddToCart = (quantity) => {
    // Lógica para agregar el producto al carrito con la cantidad especificada
    // Esto puede incluir la actualización de un estado de carrito global o el uso de un contexto.
    // ...

    // Después de agregar al carrito, actualiza cartCount
    setCartCount(cartCount + quantity);
  };

  return (
    <div className="container mt-4">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <img src={item.imagen} className="img-fluid" alt={item.nombre} />
          <h2>{item.nombre}</h2>
          <p>{item.descripcion}</p>
          <p>Precio: $ {item.precio}</p>
          <p>Disponibilidad: {item.stock > 0 ? "En stock" : "Agotado"}</p>
        </div>
      </div>
      <div className="row mt-4">
        <div className="col-md-6 offset-md-3">
          <ItemCount stockItems={item.stock} onAddToCart={handleAddToCart} />
          {cartCount > 0 && (
            <p>Elementos en el carrito: {cartCount}</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default ItemDetail;
