import React, { useRef } from "react";
import { Link } from "react-router-dom";

const Item = ({ item }) => {

  const contador = useRef(0);

  const handleClick = (condicion) => {
    if (condicion) {
      contador.current++;
    } else {
      contador.current--;
    }
    console.log(`El contador va por: ${contador.current}`);
  };

  console.log(`El componente se renderizo`);

  return (
    <Link to={"/item/" + item.id} className="text-decoration-none">
      <div className="card">
        <img src={item.imagen} className="card-img-top" alt={item.nombre} />
        <div className="card-body text-center">
          <h5 className="card-title">{item.nombre}</h5>
          <p className="card-text">Precio: ${item.precio}</p>
          <p className="card-text">Stock: {item.stock}</p>

          <button onClick={() => handleClick(true)}> Sumar </button>
          <button onClick={() => handleClick(true)}> Restar </button>
        </div>
      </div>
    </Link>
  );
};

export default Item;
